from sbie_weinberg.dataset.demo import data 


def test_make_range_demo():

    """ JSON files for demoinput are generated by this function """        
    data.make_range_demo(outfile='range.json')
    
    data.make_inputdata_combination(infile='range.json', \
        combi_numbers=[1,2], outputheader='demoinput_') 

    assert True 


def test_make_range_dream2015():
    
    data.make_range_dream2015(outfile='range_dream2015.json')

    data.make_inputdata_combination(infile='range_dream2015.json', \
        combi_numbers=[2], outputheader='demoinput_dream2015_') 

    assert True


